					const maxTryTime = 120;
					return new Promise((resolve, reject) => {
						let interval = setInterval(() => {
							tabContainer = body.querySelector(targetContainer);
							if (tabContainer) {
								clearInterval(interval);
								resolve(tabContainer);
							}
							if ((++tryTime) === maxTryTime) {
								clearInterval(interval);
								reject();
							}
						}, 500);
					});
